$(document).ready(function(){	
	$('.daterange').each(function(){
		var $this = $(this);
		$this.find('.start, .end').datetimepicker({locale:"uk",useCurrent:false,ignoreReadonly:true,format:"YYYY-MM-DD"})
		$this.find('.start').datetimepicker().on('dp.change', function(event){ $this.find('.end').data("DateTimePicker").minDate(event.date); });
		$this.find('.end').datetimepicker().on('dp.change', function(event){ $this.find('.start').data("DateTimePicker").maxDate(event.date); });
	});
});