tinymce.PluginManager.add("docsembed",function(e){
	function o(t){
		var o=e.windowManager.open({
			title:"Вставити документ",
			body:{
				name: 'url',
				type: 'textbox',
				label: 'Посилання',
				minWidth: 500
			},
			onSubmit:function(o){
				e.focus(),
				e.undoManager.transact(function(){
					e.execCommand("mceInsertContent",!1,'[doc=' + o.data.url + ']')
				}),
				e.nodeChanged()
			}
		});
	}
	
	e.addButton("docsembedbutton",{icon:"newdocument",tooltip:"Вставити документ",onclick:o})
	e.addMenuItem("docsembedbutton",{icon:"newdocument",text:"Вставити документ",onclick:o})
});